(function () {
  function applySize(mobile) {
    document.querySelector(".uu-app-loading-svg")?.setAttribute("viewBox", mobile ? "0 0 144 144" : "0 0 252 252");
    var circleEl = document.querySelector(".uu-app-loading-svg circle");
    if (circleEl) {
      circleEl.setAttribute("cx", mobile ? "72" : "126");
      circleEl.setAttribute("cy", mobile ? "72" : "126");
      circleEl.setAttribute("r", mobile ? "69" : "123");
    }
  }
  var media = window.matchMedia("screen and (min-width: 481px)");
  media.onchange = (e) => applySize(!e.matches);
  if (!media.matches) applySize(true);

  var env = window.uu5Environment || (window.UU5 || {}).Environment || {}
  var visual = env["uu_plus4u5g02_uuApp"];
  if (visual) {
    if (visual.background) document.body.style.background = visual.background;
    if (visual.foreground) {
      document.body.style.color = visual.foreground;
      document.querySelector(".uu-app-loading").style.color = visual.foreground;
      document.querySelector(".uu-app-loading-name").style.color = visual.foreground;
    }
    if (visual.name) {
      document.querySelector(".uu-app-loading-name").appendChild(document.createTextNode(visual.name));
      if (visual.nameForeground) document.querySelector(".uu-app-loading-name").style.color = visual.nameForeground;
    }
    if (visual.icon) {
      fetch(visual.icon).then(function (response) {
        var contentType = response.headers.get("Content-Type");
        var isSvg = /image\/svg/.test(contentType);
        return isSvg
          ? response.text().then(function (text) { return { type: "svg", text: text }; })
          : response.blob().then(function (blob) { return { type: "blob", blob: blob }});
      }).then(function (iconInfo) {
        var imageStyle = "position: absolute; top: 0; right: 0; bottom: 0; left: 0; object-fit: contain; object-position: center; margin: auto; max-width: 100%; max-height: 100%; z-index: -1;";
        var el;
        if (iconInfo.type === "svg") {
          var div = document.createElement("div");
          div.innerHTML = iconInfo.text;
          var svgEl = div.querySelector("svg");
          if (svgEl) svgEl.style.cssText = imageStyle;
          el = svgEl;
        } else {
          var img = document.createElement("img");
          img.src = URL.createObjectURL(iconInfo.blob);
          img.style.cssText = imageStyle;
          el = img;
        }
        if (el) {
          var svgContainerEl = document.querySelector(".uu-app-loading-svg-container");
          if (svgContainerEl) svgContainerEl.appendChild(el);
        }
      });
    }
  }
  document.querySelector(".uu-app-loading").setAttribute("data-visual-ready", "");
})();

body.script:
function handleUuAppScriptError(error) {
  console.error(error);
  var targetEl = document.getElementById("uuApp");
  if (!targetEl) document.body.appendChild(targetEl = document.createElement("div"));
  else targetEl.innerHTML = "";

  var descKey = navigator.onLine === false ? "offline" : "error";
  var errorEl = document.querySelector('#uu-app-script-error [lang="en"]');
  var descEl = errorEl.querySelector('.uu-app-script-error-description[data-key="' + descKey + '"]');
  var toggleEl = errorEl.querySelector(".uu-app-script-error-toggle");
  var detailsEl = errorEl.querySelector(".uu-app-script-error-details");
  var helpdeskEl = errorEl.querySelector(".uu-app-script-error-helpdesk");
  var refreshEl = descEl.querySelector(".uu-app-script-error-refresh");

  var uu5Env = typeof uu5Environment !== "undefined" ? uu5Environment || {} : typeof UU5 !== "undefined" ? (UU5 || {}).Environment || {} : {};
  if (uu5Env["uu_plus4u5g02_helpdeskUri"]) helpdeskEl.href = uu5Env["uu_plus4u5g02_helpdeskUri"];
  if (refreshEl && typeof Uu5Loader !== "undefined") {
    refreshEl.href = location.href;
    refreshEl.addEventListener("click", function refreshPage(e) {
      e.preventDefault();
      e.stopPropagation();
      Uu5Loader.refreshCache().then(function () {
        location.reload();
      });
    });
  }
  descEl.hidden = false;
  toggleEl.addEventListener("click", function () { detailsEl.hidden = false; this.style.cssText = "display: none"; detailsEl.focus(); });
  detailsEl.appendChild(document.createTextNode(error != null ? (error.stack || error) + "" : error + ""));
  detailsEl.addEventListener("keydown", function (e) {
    if ((e.ctrlKey || e.metaKey) && e.keyCode === 65) { e.preventDefault(); e.stopPropagation(); getSelection().selectAllChildren(this); }
  });
  targetEl.appendChild(errorEl);

  var loadingEl = document.getElementById("uuAppLoading");
  if (loadingEl) loadingEl.parentNode.removeChild(loadingEl);
}

if (!window.Uu5Loader) handleUuAppScriptError(new Error("Uu5Loader failed to load."));
else Uu5Loader.import("./index.js").then(function (Index) {
  Index.render("uuApp");
}).catch(handleUuAppScriptError);
